CREATE TABLE "ON_SITE_CONF"
(
    "ID"              NUMBER GENERATED BY DEFAULT ON NULL AS IDENTITY MINVALUE 1 MAXVALUE 9999999999999999999999999999 INCREMENT BY 1 START WITH 25 CACHE 20 NOORDER NOCYCLE NOKEEP NOSCALE NOT NULL ENABLE,
    SITE              VARCHAR2(32) not null,
    MES_TYPE          VARCHAR2(32) not null,
    DRIVER_CLASS_NAME VARCHAR2(200),
    CONNECTION_STRING VARCHAR(1000),
    DB_USERNAME       VARCHAR(100),
    DB_PASSWORD       VARCHAR2(100),
    SQL_LTM_PRODUCT_REPLACEMENT VARCHAR2(2000)
) SEGMENT CREATION IMMEDIATE
  PCTFREE 10 PCTUSED 40 INITRANS 1 MAXTRANS 255
  NOCOMPRESS LOGGING
  STORAGE(INITIAL 65536 NEXT 1048576 MINEXTENTS 1 MAXEXTENTS 2147483645
  PCTINCREASE 0 FREELISTS 1 FREELIST GROUPS 1
  BUFFER_POOL DEFAULT FLASH_CACHE DEFAULT CELL_FLASH_CACHE DEFAULT)
  TABLESPACE "DP_DATA";

CREATE UNIQUE INDEX "ON_SITE_CONF_ID_UINDEX" ON "ON_SITE_CONF" ("ID") PCTFREE 10 INITRANS 2 MAXTRANS 255 COMPUTE STATISTICS
  STORAGE(INITIAL 65536 NEXT 1048576 MINEXTENTS 1 MAXEXTENTS 2147483645
  PCTINCREASE 0 FREELISTS 1 FREELIST GROUPS 1
  BUFFER_POOL DEFAULT FLASH_CACHE DEFAULT CELL_FLASH_CACHE DEFAULT)
  TABLESPACE "DP_DATA";

